import React from "react";
import "./App.css";
import SongInfo from "./SongInfo";
//import NewSong from "./NewSong";
import Navbar from "./Navbar";
import Home from "./Home/Home";
// import JohnLennon from './JohnLennon/JohnLennon'
import PaulMcCartney from "./PaulMcCartney/PaulMcCartney";
import GeorgeHarrison from "./GeorgeHarrison/GeorgeHarrison";
import RingoStarr from "./RingoStarr/RingoStarr";
import { Route, Link } from "react-router-dom";
import SignUpForm from "../SignUpForm/SignUpForm";
import LogInForm from "../LogInForm/LogInForm";
import LogOut from "../LogOut/LogOut";
import "./App.css";
import UserNavBar from "../NavBar/NavBar";

class App extends Component {
  constructor() {
    super();

    this.state = {
      email: "",
      password: "",
      isLoggedIn: false,
    };

    this.handleLogOut = this.handleLogOut.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.handleLogIn = this.handleLogIn.bind(this);
    this.handleSignUp = this.handleSignUp.bind(this);
  }

  componentDidMount() {
    if (localStorage.token) {
      this.setState({
        isLoggedIn: true,
      });
    } else {
      this.setState({
        isLoggedIn: false,
      });
    }
  }

  handleLogOut() {}

  handleInput(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handleSignUp(e) {}

  handleLogIn(e) {}
  render() {
    return (
      <div>
        <div className="App">
          <div>
            <NavBar isLoggedIn={this.state.isLoggedIn} />
            <div className="body">
              <Switch>
                <Route
                  path="/signup"
                  render={(props) => {
                    return (
                      <SignUpForm
                        isLoggedIn={this.state.isLoggedIn}
                        handleInput={this.handleInput}
                        handleSignUp={this.handleSignUp}
                      />
                    );
                  }}
                />
                <Route
                  path="/logout"
                  render={(props) => {
                    return (
                      <LogOut
                        isLoggedIn={this.state.isLoggedIn}
                        handleLogOut={this.handleLogOut}
                      />
                    );
                  }}
                />
                <Route
                  path="/login"
                  render={(props) => {
                    return (
                      <LogInForm
                        isLoggedIn={this.state.isLoggedIn}
                        handleInput={this.handleInput}
                        handleLogIn={this.handleLogIn}
                      />
                    );
                  }}
                />
                />
              </Switch>
            </div>
            <div className="App">
              <Navbar></Navbar>
              {/*  */}
              <main>
                <Route path="/" exact component={Home} />
                {/*    <Route
      path="/JohnLennon/"
      component={JohnLennon}
    /> */}
                <Route path="/PaulMcCartney" exact component={PaulMcCartney} />
                <Route
                  path="/GeorgeHarrison"
                  exact
                  component={GeorgeHarrison}
                />
                <Route path="/RingoStarr" exact component={RingoStarr} />
              </main>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
