import React from "react";
import "./App.css";
import SongInfo from "./SongInfo";
//import NewSong from "./NewSong";
import Navbar from "./Navbar";
import Home from "./Home/Home";
// import JohnLennon from './JohnLennon/JohnLennon'
import PaulMcCartney from "./PaulMcCartney/PaulMcCartney";
import GeorgeHarrison from "./GeorgeHarrison/GeorgeHarrison";
import RingoStarr from "./RingoStarr/RingoStarr";
import { Route, Link } from "react-router-dom";

function App() {
  const song = {
    artist: "artistName",
    song: "songTitle",
    lyrics: "songLyrics",
  };

  return (
    <div>
      <div className="App">
        <div>
          {/* <NavBar isLoggedIn={this.state.isLoggedIn} /> */}
          <div className="body">
            <Switch>
              <Route
                path="/signup"
                render={(props) => {
                  return (
                    <SignUpForm
                      isLoggedIn={this.state.isLoggedIn}
                      handleInput={this.handleInput}
                      handleSignUp={this.handleSignUp}
                    />
                  );
                }}
              />
              <Route
                path="/logout"
                render={(props) => {
                  return (
                    <LogOut
                      isLoggedIn={this.state.isLoggedIn}
                      handleLogOut={this.handleLogOut}
                    />
                  );
                }}
              />
              <Route
                path="/login"
                render={(props) => {
                  return (
                    <LogInForm
                      isLoggedIn={this.state.isLoggedIn}
                      handleInput={this.handleInput}
                      handleLogIn={this.handleLogIn}
                    />
                  );
                }}
              />
              <Route
                path="/"
                render={() => {
                  return <DogList isLoggedIn={this.state.isLoggedIn} />;
                }}
              />
            </Switch>
          </div>
          <div className="App">
            <Navbar></Navbar>
            {/*  */}
            <main>
              <Route path="/" exact component={Home} />
              {/*    <Route
      path="/JohnLennon/"
      component={JohnLennon}
    /> */}
              <Route path="/PaulMcCartney" exact component={PaulMcCartney} />
              <Route path="/GeorgeHarrison" exact component={GeorgeHarrison} />
              <Route path="/RingoStarr" exact component={RingoStarr} />
            </main>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
